/* 1. BORRAR TABLAS ANTERIORES */
DROP TABLE IF EXISTS elpais;
DROP TABLE IF EXISTS elmundo;

/* 2. CREAR TABLA EL MUNDO (POSTGRESQL) */
CREATE TABLE elmundo (
    id SERIAL PRIMARY KEY,
    titulo VARCHAR(500),
    link VARCHAR(2048) UNIQUE,
    descripcion TEXT,
    categoria VARCHAR(255),
    "fPubli" DATE,
    contenido TEXT
);

/* 3. INSERTAR DATOS EL MUNDO */

/* 4. CREAR TABLA EL PAIS (POSTGRESQL) */
CREATE TABLE elpais (
    id SERIAL PRIMARY KEY,
    titulo VARCHAR(500),
    link VARCHAR(2048) UNIQUE,
    descripcion TEXT,
    categoria VARCHAR(255),
    "fPubli" DATE,
    contenido TEXT
);

